mapper.update:
UPDATE offers_register
SET start_date=#{startDate},
    end_date=#{endDate},
    percentage=#{percentage},
    status=#{status}
WHERE product_product_id = #{productId}
  AND id_offers = #{offerId}

UPDATE offers_register
SET status=3
WHERE id_offers = #{offerId}

